mapper.update:
update User
set token = null
where token=#{token};

mapper.delete:
delete
from matchjoin
where matchid=#{matchid} and sutdent_id=#{sutdent_id};

mapper.select:
select id
from User
where psd = #{psd}
  and account = #{account}

select id
from User
where account = #{account}

select id, account
from User
where psd = #{psd}
  and account = #{account}

mapper.update:
update User
set token = #{token}
where id = #{id};
